!function(t){"use strict";angular.module("myTodomvc",[]).controller("myTodomvcController",["$scope",function(t){t.text="",t.todos=[{id:1,text:"学习",completed:!1},{id:2,text:"吃饭",completed:!1},{id:3,text:"睡觉",completed:!0}],t.add=function(){""!==t.text&&(t.todos.unshift({id:Math.random(),text:t.text,completed:!1}),t.text="")},t.remove=function(o){for(var e=0;e<t.todos.length;e++)if(o===t.todos[e].id){t.todos.splice(e,1);break}},t.clear=function(){for(var o=[],e=0;e<t.todos.length;e++)t.todos[e].completed||o.unshift(t.todos[e]);t.todos=o},t.exsitCompleted=function(){for(var o=0;o<t.todos.length;o++)if(t.todos[o].completed)return!0;return!1},t.currentId=-1,t.editing=function(o){t.currentId=o},t.save=function(){t.currentId=-1};var o=!0;t.toggleAll=function(){for(var e=0;e<t.todos.length;e++)t.todos[e].completed=o;o=!o}}])}(window);